import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [groupBy, setGroupBy] = useState("status");
  const [ordering, setOrdering] = useState("priority");

  const data = [
    { id: 1, title: "Task 1", status: "to do", priority: "high", user: "user_a" },
    { id: 2, title: "Task 2", status: "in progress", priority: "medium", user: "user_b" },
    { id: 3, title: "Task 3", status: "backlog", priority: "low", user: "user_a" },
    // Add more tasks as needed
  ];

  // Group tasks based on the selected criteria
  const groupedData = data.reduce((acc, task) => {
    const key = task[groupBy] || "Unassigned";
    if (!acc[key]) {
      acc[key] = [];
    }
    acc[key].push(task);
    return acc;
  }, {});

  return (
    <div className="app">
      <div className="top-section">
        <label htmlFor="display">Display:</label>
        <select id="display" className="display-dropdown" onChange={(e) => setGroupBy(e.target.value)}>
          <option value="status">Group by Status</option>
          <option value="user">Group by User</option>
          <option value="priority">Group by Priority</option>
        </select>
        <select className="display-dropdown" onChange={(e) => setOrdering(e.target.value)}>
          <option value="priority">Order by Priority</option>
          <option value="date">Order by Date</option>
        </select>
      </div>
      <div className="info-section">
        {Object.keys(groupedData).map((key) => (
          <div className="category" key={key}>
            <h3>{key}</h3>
            <div className="tasks">
              {groupedData[key].map((task) => (
                <div className="task" key={task.id}>
                  {task.title} - {task.priority}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
