// src/App.js
import React, { useState, useEffect } from 'react';
import ControlPanel from './components/ControlPanel'; // Ensure paths are correct
import TicketColumn from './components/TicketColumn'; // Ensure paths are correct
import { fetchTickets } from './api'; // Ensure paths are correct

function App() {
  const [tickets, setTickets] = useState([]);
  const [groupBy, setGroupBy] = useState('status'); // Default grouping by status

  useEffect(() => {
    async function loadTickets() {
      const ticketData = await fetchTickets();
      setTickets(ticketData);
    }
    loadTickets();
  }, []);

  const handleGroupChange = (group) => {
    setGroupBy(group);
  };

  return (
    <div className="kanban-board">
      <ControlPanel onGroupChange={handleGroupChange} />
      <div className="columns-container">
        <TicketColumn tickets={tickets} groupBy={groupBy} />
      </div>
    </div>
  );
}

export default App;
