import React, { useState, useEffect } from 'react';
import './App.css';

const users = ['Alice', 'Bob', 'Charlie', 'David', 'Eva']; // Example user names

function App() {
  const [tickets, setTickets] = useState([]);
  const [groupBy, setGroupBy] = useState('status'); // Default grouping by status
  const [orderBy, setOrderBy] = useState('priority'); // Default ordering by priority

  // Simulate fetching tickets with random users
  useEffect(() => {
    // Simulated ticket data with descriptive titles
    const simulatedTickets = [
      { id: 1, title: 'Fix login bug', status: 'To Do', priority: 3 },
      { id: 2, title: 'Design landing page', status: 'In Progress', priority: 2 },
      { id: 3, title: 'Set up database', status: 'Backlog', priority: 1 },
      { id: 4, title: 'Implement user authentication', status: 'To Do', priority: 4 },
      { id: 5, title: 'Create API endpoints', status: 'In Progress', priority: 0 },
      { id: 6, title: 'Write unit tests', status: 'Backlog', priority: 3 },
    ];

    // Assign random users to each ticket
    const ticketsWithUsers = simulatedTickets.map(ticket => ({
      ...ticket,
      user: users[Math.floor(Math.random() * users.length)] // Randomly assign a user
    }));

    setTickets(ticketsWithUsers);
  }, []);

  const handleGroupByChange = (e) => {
    setGroupBy(e.target.value);
  };

  const handleOrderByChange = (e) => {
    setOrderBy(e.target.value);
  };

  // Group tickets based on selected groupBy option
  const groupedTickets = tickets.reduce((acc, ticket) => {
    const key = groupBy === 'user' ? ticket.user : ticket[groupBy] || 'No Category'; // Use user for grouping
    if (!acc[key]) acc[key] = [];
    acc[key].push(ticket);
    return acc;
  }, {});

  return (
    <div className="kanban-board">
      <div className="display-options">
        <label htmlFor="display">Display:</label>
        <select id="display">
          <option value="group-by">Group By</option>
          <option value="order-by">Order By</option>
        </select>

        <label htmlFor="group-by">Group By:</label>
        <select id="group-by" value={groupBy} onChange={handleGroupByChange}>
          <option value="status">Status</option>
          <option value="user">User</option>
          <option value="priority">Priority</option>
        </select>

        <label htmlFor="order-by">Order By:</label>
        <select id="order-by" value={orderBy} onChange={handleOrderByChange}>
          <option value="priority">Priority</option>
          <option value="title">Title</option>
        </select>
      </div>

      {/* Horizontal line */}
      <hr className="divider" />

      {/* Displaying all categories with tickets */}
      <div className="ticket-columns">
        {Object.keys(groupedTickets).map((category) => (
          <div key={category} className="ticket-column">
            <div className="category-name">{category}</div>
            <div className="ticket-list">
              {groupedTickets[category].length > 0 ? (
                groupedTickets[category].map((ticket) => (
                  <div key={ticket.id} className="ticket-card">
                    {ticket.title}
                  </div>
                ))
              ) : (
                <div className="ticket-card empty">No tickets</div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
