import React, { useState } from 'react';
import './App.css';

// Import category images
const images = {
  'To Do': require('./icons_FEtask/todo.png'), // Replace with your actual image file names
  'In Progress': require('./icons_FEtask/inprogress.png'),
  'Backlog': require('./icons_FEtask/backlog.png'),
  'User A': require('./icons_FEtask/user_a.png'), // Add user-specific images
  'User B': require('./icons_FEtask/user_b.png'),
  'User C': require('./icons_FEtask/user_c.png'),
  'User D': require('./icons_FEtask/user_d.png'),
  // Add more mappings as necessary
};

const initialTasks = [
  { id: 1, title: 'Implement feature A', status: 'To Do', assignedUser: 'User A', priority: 3 },
  { id: 2, title: 'Fix bug B', status: 'In Progress', assignedUser: 'User B', priority: 1 },
  { id: 3, title: 'Review code C', status: 'Backlog', assignedUser: 'User A', priority: 2 },
  { id: 4, title: 'Write documentation', status: 'To Do', assignedUser: 'User C', priority: 4 },
  { id: 5, title: 'Update UI', status: 'In Progress', assignedUser: 'User D', priority: 0 },
];

const App = () => {
  const [tasks] = useState(initialTasks);
  const [groupBy, setGroupBy] = useState('Status');
  const [ordering, setOrdering] = useState('Priority');
  const [displayOptions, setDisplayOptions] = useState(false);

  const getGroupedTasks = () => {
    const grouped = {};
    tasks.forEach(task => {
      const groupKey = groupBy === 'User' ? task.assignedUser : task[groupBy.toLowerCase()];
      if (!grouped[groupKey]) {
        grouped[groupKey] = [];
      }
      grouped[groupKey].push(task);
    });

    return Object.entries(grouped).map(([key, value]) => ({
      key,
      tasks: value.sort((a, b) => {
        if (ordering === 'Priority') {
          return b.priority - a.priority;
        } else {
          return a.title.localeCompare(b.title);
        }
      }),
    }));
  };

  const groupedTasks = getGroupedTasks();

  return (
    <div className="kanban-board">
      <div className="display-options">
        <div className="dropdown">
          <button onClick={() => setDisplayOptions(!displayOptions)}>
            &#x1F5C3; Display {/* Unicode for categories symbol */}
          </button>
          {displayOptions && (
            <div className="dropdown-content">
              <div>
                <label htmlFor="groupBy">Group By:</label>
                <select id="groupBy" value={groupBy} onChange={e => setGroupBy(e.target.value)}>
                  <option value="Status">Status</option>
                  <option value="User">User</option>
                  <option value="Priority">Priority</option>
                </select>
              </div>
              <div>
                <label htmlFor="ordering">Ordering:</label>
                <select id="ordering" value={ordering} onChange={e => setOrdering(e.target.value)}>
                  <option value="Priority">Priority</option>
                  <option value="Title">Title</option>
                </select>
              </div>
            </div>
          )}
        </div>
      </div>
      <div className="ticket-info">
        <div className="ticket-columns">
          {groupedTasks.map(group => (
            <div key={group.key} className="ticket-column">
              <div className="category-name">
                <img src={images[group.key] || images['default']} alt={`${group.key} icon`} className="category-icon" />
                {group.key || 'Unassigned'}
              </div>
              <div className="ticket-list">
                {group.tasks.length > 0 ? (
                  group.tasks.map(task => (
                    <div key={task.id} className="ticket-card">
                      {task.title} (Priority: {task.priority})
                    </div>
                  ))
                ) : (
                  <div className="empty">No tasks</div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
