import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [groupBy, setGroupBy] = useState('Status');
  const [ordering, setOrdering] = useState('Priority');

  const tasks = [
    { id: 1, title: 'Task 1', status: 'To Do', priority: 'High' },
    { id: 2, title: 'Task 2', status: 'In Progress', priority: 'Medium' },
    { id: 3, title: 'Task 3', status: 'Backlog', priority: 'Low' },
    { id: 4, title: 'Task 4', status: 'To Do', priority: 'High' },
    { id: 5, title: 'Task 5', status: 'In Progress', priority: 'Medium' },
  ];

  const groupedTasks = {};
  tasks.forEach(task => {
    const group = task[groupBy.toLowerCase()];
    if (!groupedTasks[group]) {
      groupedTasks[group] = [];
    }
    groupedTasks[group].push(task);
  });

  return (
    <div className="app-container">
      <div className="top-section">
        <div className="display-dropdown">
          <label htmlFor="display">Display:</label>
          <select id="display" className="dropdown">
            <option value="groupBy">Group By</option>
            <option value="ordering">Ordering</option>
          </select>
        </div>
        <div className="nested-dropdowns">
          <label htmlFor="groupBySelect">Group By:</label>
          <select
            id="groupBySelect"
            value={groupBy}
            onChange={e => setGroupBy(e.target.value)}
            className="dropdown"
          >
            <option value="Status">Status</option>
            <option value="Priority">Priority</option>
            <option value="User">User</option>
          </select>
          <label htmlFor="orderingSelect">Ordering:</label>
          <select
            id="orderingSelect"
            value={ordering}
            onChange={e => setOrdering(e.target.value)}
            className="dropdown"
          >
            <option value="Priority">Priority</option>
            <option value="Alphabetical">Alphabetical</option>
          </select>
        </div>
      </div>
      <div className="information-section">
        {Object.keys(groupedTasks).map(group => (
          <div key={group} className="category">
            <h3 className="category-title">{group.charAt(0).toUpperCase() + group.slice(1)}</h3>
            {groupedTasks[group].map(task => (
              <div key={task.id} className="task-card">
                {task.title}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
