// src/App.js
import React, { useState, useEffect } from 'react';
import ControlPanel from './components/ControlPanel';
import TicketColumn from './components/TicketColumn';
import { fetchTickets } from './api';

function App() {
  const [tickets, setTickets] = useState([]);
  const [groupBy, setGroupBy] = useState('status'); // Default grouping by status
  const [orderBy, setOrderBy] = useState('priority'); // Default ordering by priority

  useEffect(() => {
    async function loadTickets() {
      const ticketData = await fetchTickets();
      setTickets(ticketData);
    }
    loadTickets();
  }, []);

  const handleGroupChange = (group) => {
    setGroupBy(group);
  };

  const handleOrderChange = (order) => {
    setOrderBy(order);
  };

  return (
    <div className="kanban-board">
      <ControlPanel onGroupChange={handleGroupChange} onOrderChange={handleOrderChange} />
      <div className="columns-container">
        <TicketColumn tickets={tickets} groupBy={groupBy} orderBy={orderBy} />
      </div>
    </div>
  );
}

export default App;
