// src/components/TicketColumn.js
import React from 'react';
import TicketCard from './TicketCard';

function TicketColumn({ tickets, groupBy, orderBy }) {
  const groupTickets = () => {
    const grouped = {};
    tickets.forEach(ticket => {
      const key = ticket[groupBy] || 'No Group';
      if (!grouped[key]) grouped[key] = [];
      grouped[key].push(ticket);
    });
    return grouped;
  };

  const sortTickets = (groupedTickets) => {
    const sortedTickets = {};
    Object.keys(groupedTickets).forEach((key) => {
      sortedTickets[key] = groupedTickets[key].sort((a, b) => {
        if (orderBy === 'priority') {
          return b.priority - a.priority; // Descending order
        }
        return a.title.localeCompare(b.title); // Ascending order by title
      });
    });
    return sortedTickets;
  };

  const groupedTickets = groupTickets();
  const sortedGroupedTickets = sortTickets(groupedTickets);

  return (
    <div className="ticket-columns">
      <div className="ticket-row">
        {Object.keys(sortedGroupedTickets).map((group) => (
          <div key={group} className="ticket-column">
            <h3 className="category-name">{group}</h3>
            <div className="ticket-list">
              {sortedGroupedTickets[group].map((ticket) => (
                <TicketCard key={ticket.id} ticket={ticket} groupBy={groupBy} />
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TicketColumn;
