import React, { useState } from 'react';
import './App.css';

// Importing category images
import todoImage from './icons_FEtask/To-do.svg';
import inProgressImage from './icons_FEtask/in-progress.svg';
import doneImage from './icons_FEtask/Done.svg';
import backlogImage from './icons_FEtask/Backlog.svg';
import highPriorityImage from './icons_FEtask/Img-high.svg';
import lowPriorityImage from './icons_FEtask/Img-low.svg';

// Define available category options
const statusCategories = ['To Do', 'In Progress', 'Done', 'Backlog'];
const priorityCategories = ['High Priority', 'Low Priority'];
const userCategories = ['Alice', 'Bob', 'Charlie', 'David']; // Add more users as needed

const categoryImages = {
  'To Do': todoImage,
  'In Progress': inProgressImage,
  'Done': doneImage,
  'Backlog': backlogImage,
  'High Priority': highPriorityImage,
  'Low Priority': lowPriorityImage,
};

const initialTasks = [
  { id: 1, title: 'Implement feature A', status: 'To Do', assignedUser: 'Alice', priority: 'High Priority' },
  { id: 2, title: 'Fix bug B', status: 'In Progress', assignedUser: 'Bob', priority: 'Low Priority' },
  { id: 3, title: 'Review code C', status: 'Backlog', assignedUser: 'Charlie', priority: 'High Priority' },
  { id: 4, title: 'Write documentation', status: 'To Do', assignedUser: 'Alice', priority: 'Low Priority' },
  { id: 5, title: 'Update UI', status: 'In Progress', assignedUser: 'David', priority: 'Low Priority' },
];

const App = () => {
  const [tasks] = useState(initialTasks);
  const [groupBy, setGroupBy] = useState('Status');
  const [ordering, setOrdering] = useState('Priority');
  const [displayOptions, setDisplayOptions] = useState(false);

  const getGroupedCategories = () => {
    if (groupBy === 'Status') {
      return statusCategories;
    } else if (groupBy === 'Priority') {
      return priorityCategories;
    } else if (groupBy === 'User') {
      return userCategories;
    }
    return [];
  };

  const getGroupedTasks = () => {
    const grouped = {};
    tasks.forEach(task => {
      const groupKey = groupBy === 'User' ? task.assignedUser : task[groupBy.toLowerCase()];
      if (!grouped[groupKey]) {
        grouped[groupKey] = [];
      }
      grouped[groupKey].push(task);
    });

    // Ensure all categories appear, even if no tasks are assigned
    const allCategories = getGroupedCategories();
    allCategories.forEach(category => {
      if (!grouped[category]) {
        grouped[category] = []; // Empty array for categories with no tasks
      }
    });

    return Object.entries(grouped).map(([key, value]) => ({
      key,
      tasks: value.sort((a, b) => {
        if (ordering === 'Priority') {
          return b.priority.localeCompare(a.priority);
        } else {
          return a.title.localeCompare(b.title);
        }
      }),
    }));
  };

  const groupedTasks = getGroupedTasks();

  return (
    <div className="kanban-board">
      <div className="display-options">
        <div className="dropdown">
          <button onClick={() => setDisplayOptions(!displayOptions)}>Display</button>
          {displayOptions && (
            <div className="dropdown-content">
              <div>
                <label htmlFor="groupBy">Group By:</label>
                <select id="groupBy" value={groupBy} onChange={e => setGroupBy(e.target.value)}>
                  <option value="Status">Status</option>
                  <option value="User">User</option>
                  <option value="Priority">Priority</option>
                </select>
              </div>
              <div>
                <label htmlFor="ordering">Ordering:</label>
                <select id="ordering" value={ordering} onChange={e => setOrdering(e.target.value)}>
                  <option value="Priority">Priority</option>
                  <option value="Title">Title</option>
                </select>
              </div>
            </div>
          )}
        </div>
      </div>
      <div className="ticket-info">
        <div className="ticket-columns">
          {groupedTasks.map(group => (
            <div key={group.key} className="ticket-column">
              <div className="category-name">
                <img src={categoryImages[group.key] || '/images/default.png'} alt={group.key} style={{ width: '20px', marginRight: '5px' }} />
                {group.key || 'Unassigned'}
              </div>
              <div className="ticket-list">
                {group.tasks.length > 0 ? (
                  group.tasks.map(task => (
                    <div key={task.id} className="ticket-card">
                      <input type="checkbox" className="checkbox" />
                      {task.title} (Priority: {task.priority})
                    </div>
                  ))
                ) : (
                  <div className="empty">No tasks</div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
