import React, { useState, useEffect } from 'react';
import { fetchTickets } from './api';
import ControlPanel from './components/ControlPanel';
import TicketColumn from './components/TicketColumn';
import './App.css';

const App = () => {
  const [tickets, setTickets] = useState([]);
  const [grouping, setGrouping] = useState('status');
  const [sortOption, setSortOption] = useState('priority');

  // Fetch tickets on component mount
  useEffect(() => {
    fetchTickets().then(fetchedTickets => setTickets(fetchedTickets));
  }, []);

  // Grouping Functions
  const groupByStatus = (tickets) => { /* Group tickets by status logic */ };
  const groupByUser = (tickets) => { /* Group tickets by user logic */ };
  const groupByPriority = (tickets) => { /* Group tickets by priority logic */ };

  // Sorting Functions
  const sortByPriority = (tickets) => { /* Sort by priority logic */ };
  const sortByTitle = (tickets) => { /* Sort by title logic */ };

  // Dynamic ticket grouping based on the user's selection
  const groupedTickets = (() => {
    switch (grouping) {
      case 'status': return groupByStatus(tickets);
      case 'user': return groupByUser(tickets);
      case 'priority': return groupByPriority(tickets);
      default: return {};
    }
  })();

  const sortedTickets = (ticketGroup) => {
    switch (sortOption) {
      case 'priority': return sortByPriority(ticketGroup);
      case 'title': return sortByTitle(ticketGroup);
      default: return ticketGroup;
    }
  };

  return (
    <div>
      <ControlPanel 
        onGroupingChange={setGrouping} 
        onSortChange={setSortOption} 
      />
      <div className="kanban-board">
        {Object.keys(groupedTickets).map(group => (
          <TicketColumn 
            key={group} 
            title={group} 
            tickets={sortedTickets(groupedTickets[group])} 
          />
        ))}
      </div>
    </div>
  );
};

export default App;
