import React, { useState } from 'react';
import './App.css';

// Import SVG files as React components
import { ReactComponent as ToDoIcon } from 'src/icons_FEtask/To-do.svg';


const initialTasks = [
  { id: 1, title: 'Implement feature A', status: 'To Do', assignedUser: 'Alice', priority: 3 },
  { id: 2, title: 'Fix bug B', status: 'In Progress', assignedUser: 'Bob', priority: 1 },
  { id: 3, title: 'Review code C', status: 'Backlog', assignedUser: 'Charlie', priority: 2 },
  { id: 4, title: 'Write documentation', status: 'To Do', assignedUser: 'Alice', priority: 4 },
  { id: 5, title: 'Update UI', status: 'In Progress', assignedUser: 'David', priority: 0 },
];

const App = () => {
  const [tasks] = useState(initialTasks);
  const [groupBy, setGroupBy] = useState('Status');
  const [ordering, setOrdering] = useState('Priority');
  const [displayOptions, setDisplayOptions] = useState(false);

  // Function to return the corresponding SVG component for a category
  const getCategoryIcon = (key) => {
    switch (key) {
      case 'To Do':
        return <ToDoIcon className="category-icon" />;
      case 'In Progress':
        return <InProgressIcon className="category-icon" />;
      case 'Backlog':
        return <BacklogIcon className="category-icon" />;
      default:
        return null; // or you can return a default icon if needed
    }
  };

  const getGroupedTasks = () => {
    const grouped = {};
    tasks.forEach(task => {
      const groupKey = groupBy === 'User' ? task.assignedUser : task[groupBy.toLowerCase()];
      if (!grouped[groupKey]) {
        grouped[groupKey] = [];
      }
      grouped[groupKey].push(task);
    });

    return Object.entries(grouped).map(([key, value]) => ({
      key,
      tasks: value.sort((a, b) => {
        if (ordering === 'Priority') {
          return b.priority - a.priority;
        } else {
          return a.title.localeCompare(b.title);
        }
      }),
    }));
  };

  const groupedTasks = getGroupedTasks();

  return (
    <div className="kanban-board">
      <div className="display-options">
        <div className="dropdown">
          <button onClick={() => setDisplayOptions(!displayOptions)}>Display</button>
          {displayOptions && (
            <div className="dropdown-content">
              <div>
                <label htmlFor="groupBy">Group By:</label>
                <select id="groupBy" value={groupBy} onChange={e => setGroupBy(e.target.value)}>
                  <option value="Status">Status</option>
                  <option value="User">User</option>
                  <option value="Priority">Priority</option>
                </select>
              </div>
              <div>
                <label htmlFor="ordering">Ordering:</label>
                <select id="ordering" value={ordering} onChange={e => setOrdering(e.target.value)}>
                  <option value="Priority">Priority</option>
                  <option value="Title">Title</option>
                </select>
              </div>
            </div>
          )}
        </div>
      </div>
      <div className="ticket-info">
        <div className="ticket-columns">
          {groupedTasks.map(group => (
            <div key={group.key} className={`ticket-column category-${group.key.replace(/\s+/g, '-').toLowerCase()}`}>
              <div className="category-header">
                {getCategoryIcon(group.key)}
                <div className="category-name">{group.key || 'Unassigned'}</div>
              </div>
              <div className="ticket-list">
                {group.tasks.length > 0 ? (
                  group.tasks.map(task => (
                    <div key={task.id} className="ticket-card">
                      <input type="checkbox" className="checkbox" />
                      {task.title} (Priority: {task.priority})
                    </div>
                  ))
                ) : (
                  <div className="empty">No tasks</div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
