import React, { useEffect, useState } from "react";
import "./App.css";

// Sample task data
const initialTasks = [
    { id: 1, title: "Fix bug in app", status: "To Do", priority: 3 },
    { id: 2, title: "Implement new feature", status: "In Progress", priority: 4 },
    { id: 3, title: "Write documentation", status: "Backlog", priority: 2 },
    { id: 4, title: "Test application", status: "To Do", priority: 1 },
];

// Sample icons
const icons = {
    "To Do": require("./icons_FEtask/todo.png"),
    "In Progress": require("./icons_FEtask/inprogress.png"),
    "Backlog": require("./icons_FEtask/backlog.png")
};

function App() {
    const [tasks, setTasks] = useState(initialTasks);
    const [groupBy, setGroupBy] = useState("status");
    const [orderBy, setOrderBy] = useState("priority");

    const groupedTasks = tasks.reduce((acc, task) => {
        const key = task[groupBy];
        if (!acc[key]) acc[key] = [];
        acc[key].push(task);
        return acc;
    }, {});

    const sortedTasks = Object.keys(groupedTasks).map(key => ({
        category: key,
        tasks: groupedTasks[key].sort((a, b) => {
            if (orderBy === "priority") return b.priority - a.priority;
            return a.title.localeCompare(b.title);
        }),
    }));

    return (
        <div className="App">
            <div className="top-section">
                <label>
                    Display: 
                    <select onChange={(e) => setGroupBy(e.target.value)} value={groupBy}>
                        <option value="status">Group by Status</option>
                        <option value="priority">Group by Priority</option>
                    </select>
                </label>
                <label>
                    <select onChange={(e) => setOrderBy(e.target.value)} value={orderBy}>
                        <option value="priority">Order by Priority</option>
                        <option value="title">Order by Title</option>
                    </select>
                </label>
            </div>
            <div className="tasks-section">
                {sortedTasks.map((group) => (
                    <div key={group.category} className="task-group">
                        <h3>{group.category}</h3>
                        <div className="task-list">
                            {group.tasks.length === 0 ? (
                                <p>No tasks available</p>
                            ) : (
                                group.tasks.map((task) => (
                                    <div key={task.id} className="task-card">
                                        <img src={icons[task.status]} alt={`${task.status} icon`} />
                                        <div className="task-details">
                                            <h4>{task.title}</h4>
                                            <p>Priority: {task.priority}</p>
                                        </div>
                                    </div>
                                ))
                            )}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
