import React, { useState } from 'react';
import './App.css';

const tasks = [
  { id: 1, name: 'Fix bugs in code', status: 'todo', priority: 'urgent', assignedTo: 'User A' },
  { id: 2, name: 'Implement new feature', status: 'in-progress', priority: 'normal', assignedTo: 'User B' },
  { id: 3, name: 'Update documentation', status: 'backlog', priority: 'low', assignedTo: 'User C' },
  // Add more tasks as needed
];

const App = () => {
  const [groupBy, setGroupBy] = useState('status');
  const [orderBy, setOrderBy] = useState('priority');

  const groupedTasks = groupBy === 'status' 
    ? tasks.reduce((groups, task) => {
        groups[task.status] = groups[task.status] || [];
        groups[task.status].push(task);
        return groups;
      }, {})
    : {};

  return (
    <div className="app">
      <div className="top-section">
        <label htmlFor="display">Display:</label>
        <select id="display">
          <option>Group By</option>
          <option value="status">Status</option>
          <option value="priority">Priority</option>
        </select>
        <select id="ordering">
          <option>Ordering</option>
          <option value="asc">Ascending</option>
          <option value="desc">Descending</option>
        </select>
      </div>

      <div className="info-section">
        {Object.keys(groupedTasks).map((category) => (
          <div key={category} className="category">
            <h3>{category}</h3>
            <div className="task-list">
              {groupedTasks[category].map((task) => (
                <div key={task.id} className="task">
                  <strong>{task.name}</strong>
                </div>
              ))}
              {/* Display empty message if no tasks */}
              {groupedTasks[category].length === 0 && <div>No tasks available</div>}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
